class Tech(object):    def __init__(self, name: str, price: int, is_active: bool):        self.name = name        self.price = price        self.is_active = is_active    def getitem(self):        items = f"The product name is: {self.name}" \                f"\nThe product price is: {self.price}" \                f"\nThe item is {self.is_active} available"        return items    def setitem(self, color, category, brand):        _items = f"Category name: {category}\nColor: {color}\nBrand: {brand}"        return _itemsif __name__ == '__main__':    my_tech = Tech("Laptop", 20040, True)    print(my_tech.getitem())    print(my_tech.setitem("white", "computing", "apple"))class Laptop(Tech):    """    Here the classes `constructor` add new field.    :return: the `warranty` field    """    def __init__(self, name: str, price: int, is_active: bool, warranty):        Tech.__init__(self, name, price, is_active)        self.warranty = warranty    def get_warranty(self):        return self.warrantyif __name__ == '__main__':    laptop = Laptop("Laptop", 20040, True, '2 year service warranty')    print(laptop.get_warranty())