class LinkListNode(object):    def __init__(self, value, nextNode=None):        self.value = value        self.nextNode = nextNode# Example node# 10 -> 20 -> 30node1 = LinkListNode(10)node2 = LinkListNode(20)node3 = LinkListNode(30)node1.nextNode = node2  # node1 -> node2node2.nextNode = node3  # node2 -> node3# node1 -> node2 -> node3current_node = node1while True:    print(f"{current_node.value} ->", end='')    if current_node.nextNode is None:        print("None")        break    current_node = current_node.nextNode