class Node(object):    def __init__(self, data_val=None):        self.data_val = data_val        self.next_val = Noneclass SLinkList(object):    def __init__(self):        self.head_val = None    def display(self):        print_val = self.head_val        while print_val is not None:            print(print_val.data_val)            print_val = print_val.next_val    def insert(self, new_data):        new_node = Node(new_data)        new_node.next_val = self.head_val        self.head_val = new_nodeif __name__ == '__main__':    list1 = SLinkList()    list1.head_val = Node('Mon')    e2 = Node('Tue')    e3 = Node('Wed')    # Link list node two second node    list1.head_val.next_val = e2    e2.next_val = e3    # list.display()    list1.insert("sun")    list1.display()