class Comprehension(object):    numbers = range(10)    def normal_list(self):        new_list = []        for n in self.numbers:            if n % 2 == 0:  # check if the element is even                new_list.append(n ** 2)  # raise that element to the power of 2 and append to the list        print(new_list)    def list_comprehension(self):        new_list = [n ** 2 for n in self.numbers if n % 2 == 0]        print(new_list)comprehension_instance = Comprehension()comprehension_instance.normal_list()print('\nBelow the list comprehension:')comprehension_instance.list_comprehension()