def numbers():    yield 1    yield 2    yield 3    yield 4    yield 5    yield 6numbers()for number in numbers():    print(number)print('\n')# Other-hand_next = numbers()print(next(_next))print(next(_next))print(next(_next))print(next(_next))print(next(_next))print(next(_next))print('\n')def generator_function():    for i in range(11):        yield ifor item in generator_function():    print(item)