class File(object):    def __init__(self):        self.username = None        self.first_name = None        self.last_name = None    def create(self):        user_data = {            self.username: input("Enter username: "),            self.first_name: input("Enter first name: "),            self.last_name: input("Enter last name: ")        }        _data = []        with open('data/document.txt', 'w', encoding='utf-8') as file:            file.write(str(user_data))            _data.append(file)            file.close()            print("Successfully created data!")    def display(self):        with open('data/document.txt', 'r') as file:            print(file.read())    def update(self, id):        pass    def remove(self):        passdata_file = File()data_file.create()# data_file.display()