# Generator iteratedef generator():    yield 1    yield 2    yield 3    yield 4    yield 5    yield "I'm learning python with mbr-sagor"gen = generator()for g in gen:    print(g)def square():    i = 1    # An Infinite loop to generate squares    while True:        yield i * i        i += 1  # Next execution resumes# Driver code to test above generatorfor num in square():    if num > 100:        break    print(num)print("\n")def even_numbers(n):    for x in range(n):        if x % 2 == 0:            yield xnum = even_numbers(10)print(next(num))print(next(num))print(next(num))print(next(num))print(next(num))print(next(num))