"""Simple example of linear searchsearch = [64, 34, 25, 12, 22, 11, 90]search_val = 10 in search:return search_val"""def linear_search(values, search_item):    search_at = 0    search_res = False    while search_at < len(values) and search_res is False:        if values[search_at] == search_item:            search_res = True        else:            search_at = search_at + 1    return search_ressearch = [64, 34, 25, 12, 22, 11, 90]print(linear_search(search, 12))print(linear_search(search, 91))