class Node(object):    def __init__(self, data):        self.data = data        self.left = None        self.right = None    def insert(self, data):        if self.data:            if data < self.data:                if self.left is None:                    self.left = Node(data)                else:                    self.left.insert(data)            elif data > self.data:                if self.right is None:                    self.right = Node(data)                else:                    self.right.insert(data)        else:            self.data = data    # search method to compare the value with nodes    def search(self, search_val):        if search_val < self.data:            if self.left is None:                return str(search_val) + " Not Found"            return self.left.search(search_val)        elif search_val > self.data:            if self.right is None:                return str(search_val) + " Not Found"            return self.right.search(search_val)        else:            print(str(self.data) + ' is found')    def display(self):        if self.left:            self.left.display()        print(self.data)        if self.right:            self.right.display()root = Node(5)root.insert(6)root.insert(8)root.insert(13)root.search(5)root.search(8)print(root.search(10))print(root.search(50))